function DateCalculator(time) {
    //msg.payload = time
    //return msg
    var thisTime = new Date(time);

    thisTime.setHours(thisTime.getHours());
    thisTime.setMinutes(thisTime.getMinutes());
    thisTime.setSeconds(0);

    //thisTime.setDate(new Date().getDate() + 1);
    thisTime.setDate(new Date().getDate())
    thisTime.setMonth(new Date().getMonth());
    thisTime.setFullYear(new Date().getFullYear());

    var milliSeconds = thisTime.getTime() + 86400000;

    thisTime = new Date(milliSeconds);

    return thisTime;
}

